import{_ as n,o as a,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 28.2-1506 Filing of initial inventories; amended or supplemental inventories.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-1506.md","filePath":"vacode/28.2-1506.md"}'),r={name:"vacode/28.2-1506.md"},s=e("h1",{id:"§-28-2-1506-filing-of-initial-inventories-amended-or-supplemental-inventories",tabindex:"-1"},[t("§ 28.2-1506 Filing of initial inventories; amended or supplemental inventories. "),e("a",{class:"header-anchor",href:"#§-28-2-1506-filing-of-initial-inventories-amended-or-supplemental-inventories","aria-label":'Permalink to "§ 28.2-1506 Filing of initial inventories; amended or supplemental inventories."'},"​")],-1),i=e("p",null,"Whenever any privately owned land in any of such counties is to be transferred, and the transferor or the transferee requests clarification as to whether any portion of such land is claimed by the Commonwealth as ungranted shores of the sea, marsh or meadowlands, the Commission shall review the matter, and, if it determines that all or any portion of such land constitutes ungranted shores of the sea, marsh or meadowlands, the Commission shall file an amended or supplemental inventory as it may deem appropriate. Any such amended or supplemental inventory shall be filed in the same offices as the initial inventory for such area. The Commission shall not prepare or file any such amended or supplemental inventory unless (1) such classification is requested by a transferor or transferee as provided herein, or (2) (a) the federal government has asserted title to any ungranted shores of the sea, marsh or meadowlands excluding the Wallops Island Naval/Aegis facility and all land adjacent thereto lying to the south that is used for a federal National Wildlife Refuge, (b) both the Governor and Attorney General concur in writing that all or any of the property to which the federal government has asserted title constitutes ungranted shores of the sea, marsh or meadowlands, and (c) such amended or supplemental inventory only asserts the Commonwealth's title to the portion of such property that constitutes ungranted shores of the sea, marsh or meadowlands as concurred in by the Governor and the Attorney General.",-1),l=e("p",null,[t("1995, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0850",target:"_blank",rel:"noreferrer"},"850"),t(".")],-1),d=[s,i,l];function h(c,m,f,p,u,v){return a(),o("div",null,d)}const _=n(r,[["render",h]]);export{g as __pageData,_ as default};
