import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 28.2-1507 Notice of filing of inventories.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-1507.md","filePath":"vacode/28.2-1507.md"}'),n={name:"vacode/28.2-1507.md"},o=t('<h1 id="§-28-2-1507-notice-of-filing-of-inventories" tabindex="-1">§ 28.2-1507 Notice of filing of inventories. <a class="header-anchor" href="#§-28-2-1507-notice-of-filing-of-inventories" aria-label="Permalink to &quot;§ 28.2-1507 Notice of filing of inventories.&quot;">​</a></h1><p>A. The Commission shall cause to be published, within ninety days following the effective date of this section, in a newspaper of general circulation published at the state capital, in a newspaper having general circulation in the counties where the initial inventories have been filed, and in such other newspapers in the Commonwealth as the Commission generally publishes notices pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-4007.03/" target="_blank" rel="noreferrer">2.2-4007.03</a>, a notice of the filing of the initial inventories. The notice shall state that any person claiming ownership of an interest in lands designated in an initial inventory as ungranted shores of the sea, marsh or meadowlands is required to assert the claim as provided in § <a href="http://law.lis.virginia.gov/vacode/28.2-1509/" target="_blank" rel="noreferrer">28.2-1509</a> within two years following the effective date of this section, or any action to assert such claim shall be barred.</p><p>B. The Commission shall cause to be published a notice of the filing of any amended or supplemental inventory in a newspaper having general circulation in the county for which such amended or supplemental inventory applies. The notice shall refer to the initial inventory and any previous amended or supplemental inventory with respect thereto, and shall state that any person claiming ownership of an interest in lands designated therein as ungranted shores of the sea, marsh or meadowlands is required to assert the claim as provided in § <a href="http://law.lis.virginia.gov/vacode/28.2-1509/" target="_blank" rel="noreferrer">28.2-1509</a> within two years following the filing of the amended or supplemental inventory, or any action to assert such claim shall be barred.</p><p>C. The failure of the Commission to comply with the requirements of this section shall not impair the Commonwealth&#39;s rights of ownership in any ungranted shores of the sea, marsh, or meadowlands.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0873" target="_blank" rel="noreferrer">873</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0916" target="_blank" rel="noreferrer">916</a>.</p>',5),r=[o];function s(l,h,c,f,d,p){return i(),a("div",null,r)}const v=e(n,[["render",s]]);export{m as __pageData,v as default};
