import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 28.2-227 Special nonresident harvester's license; fee and oath; revocation; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-227.md","filePath":"vacode/28.2-227.md"}`),n={name:"vacode/28.2-227.md"},o=r('<h1 id="§-28-2-227-special-nonresident-harvester-s-license-fee-and-oath-revocation-penalty" tabindex="-1">§ 28.2-227 Special nonresident harvester&#39;s license; fee and oath; revocation; penalty. <a class="header-anchor" href="#§-28-2-227-special-nonresident-harvester-s-license-fee-and-oath-revocation-penalty" aria-label="Permalink to &quot;§ 28.2-227 Special nonresident harvester&#39;s license; fee and oath; revocation; penalty.&quot;">​</a></h1><p>A. Any nonresident desiring to take or catch marine fish, crabs or any other seafood, except oysters, clams or other mollusks, from the tidal waters of the Commonwealth for which a license is required shall pay to any officer or agent a fee for a nonresident harvester&#39;s license. The fee, to be established by the Commission, shall be no less than $350 or more than $1,150 or as subsequently revised by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>. Three hundred fifty dollars of each fee shall be credited to the Virginia Marine Products Fund as provided under § <a href="http://law.lis.virginia.gov/vacode/3.2-2705/" target="_blank" rel="noreferrer">3.2-2705</a>. The remainder of the fee shall be credited to the Marine Fishing Improvement Fund, as established pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-208/" target="_blank" rel="noreferrer">28.2-208</a>.</p><p>B. The license shall be required of each boat used in Virginia&#39;s tidal waters and shall be in addition to any other licenses required for the activity involved.</p><p>C. The nonresident shall state under oath his true name and address, the name and number of the boat being licensed, and that he will not violate any of the laws of the Commonwealth governing the taking and catching of seafood.</p><p>D. A nonresident harvester&#39;s license shall be required prior to the purchase of any other license for the harvesting of seafood. Revocation of this license in accordance with § <a href="http://law.lis.virginia.gov/vacode/28.2-232/" target="_blank" rel="noreferrer">28.2-232</a> shall constitute revocation of any other license held by the nonresident under the provisions of this subtitle. No commercial fishing license or permit shall be sold to a nonresident whose state of residence does not offer for sale the same or substantially similar license or permit to a resident of the Commonwealth.</p><p>E. Any Virginia resident who enters into a partnership or other agreement with the intent to defeat the object of this section is guilty of a Class 1 misdemeanor.</p><p>1983, c. 299, § 28.1-47.1; 1991, c. 411; 1992, c. 836; 1993, c. 245; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0155" target="_blank" rel="noreferrer">155</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0012" target="_blank" rel="noreferrer">12</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>.</p>',7),i=[o];function s(l,h,c,d,f,p){return a(),t("div",null,i)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
