import{_ as e,o as s,c as o,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 28.2-228 Licenses for purchase of fish, shellfish, or marine organisms from the catcher; fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-228.md","filePath":"vacode/28.2-228.md"}'),a={name:"vacode/28.2-228.md"},t=r('<h1 id="§-28-2-228-licenses-for-purchase-of-fish-shellfish-or-marine-organisms-from-the-catcher-fee" tabindex="-1">§ 28.2-228 Licenses for purchase of fish, shellfish, or marine organisms from the catcher; fee. <a class="header-anchor" href="#§-28-2-228-licenses-for-purchase-of-fish-shellfish-or-marine-organisms-from-the-catcher-fee" aria-label="Permalink to &quot;§ 28.2-228 Licenses for purchase of fish, shellfish, or marine organisms from the catcher; fee.&quot;">​</a></h1><p>A. Any person purchasing from the catcher clams, crabs, fish, or other seafood, except oysters, caught from the waters of the Commonwealth or the Potomac River, shall pay a license fee of (i) $50 for each place of business and (ii) $25 for each boat or motor vehicle used for buying. The Commission may subsequently revise the cost of licenses pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>.</p><p>B. Any person purchasing from the catcher oysters caught from the public grounds of the Commonwealth or the Potomac River shall pay a license fee of (i) $50 for a single place of business with one boat or motor vehicle used for buying oysters and (ii) $100 for a single place of business with multiple boats or motor vehicles used for buying oysters. The Commission may subsequently revise the cost of licenses pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>.</p><p>C. No license shall be required of any person purchasing seafood for personal consumption, any place of business which is solely a restaurant, or any person who operates a business which is subject to local license taxes under § <a href="http://law.lis.virginia.gov/vacode/58.1-3703/" target="_blank" rel="noreferrer">58.1-3703</a> and who has in his possession no more than one bushel of peeler crabs to be sold as bait.</p><p>1970, c. 726, § 28.1-119.1; 1979, c. 274; 1980, c. 218; 1984, c. 316; 1988, c. 27; 1992, c. 836; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>.</p>',5),i=[t];function n(c,h,l,f,p,m){return s(),o("div",null,i)}const d=e(a,[["render",n]]);export{g as __pageData,d as default};
