import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 28.2-302.10:1 Lifetime saltwater recreational fishing licenses for residents and nonresidents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.10:1.md","filePath":"vacode/28.2-302.10:1.md"}'),r={name:"vacode/28.2-302.10:1.md"},n=t('<h1 id="§-28-2-302-10-1-lifetime-saltwater-recreational-fishing-licenses-for-residents-and-nonresidents" tabindex="-1">§ 28.2-302.10:1 Lifetime saltwater recreational fishing licenses for residents and nonresidents. <a class="header-anchor" href="#§-28-2-302-10-1-lifetime-saltwater-recreational-fishing-licenses-for-residents-and-nonresidents" aria-label="Permalink to &quot;§ 28.2-302.10:1 Lifetime saltwater recreational fishing licenses for residents and nonresidents.&quot;">​</a></h1><p>A. Any resident or nonresident individual may apply for and receive from the Commission a lifetime saltwater recreational fishing license after payment of a fee of $250, except as provided in subsection C. This license shall be valid for the life of the individual and shall not be transferable.</p><p>B. All funds collected pursuant to this section shall be paid into the state treasury to the credit of the Virginia Saltwater Recreational Fishing Development Fund, as established by § <a href="http://law.lis.virginia.gov/vacode/28.2-302.3/" target="_blank" rel="noreferrer">28.2-302.3</a>.</p><p>C. Any resident 45 years of age or older who applies for the lifetime license authorized by this section shall receive such a license for one of the following fees based on age: age 45 through 50, $120; age 51 through 55, $90; age 56 through 60, $60; age 61 through 64, $30; and age 65 and older, $5.</p><p>D. The Commission may subsequently revise the cost of licenses in this section pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0107" target="_blank" rel="noreferrer">107</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>.</p>',6),s=[n];function o(l,c,f,d,h,g){return a(),i("div",null,s)}const u=e(r,[["render",o]]);export{_ as __pageData,u as default};
