import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 28.2-302.2 Recreational license fee; cooperative program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.2.md","filePath":"vacode/28.2-302.2.md"}'),i={name:"vacode/28.2-302.2.md"},o=r('<h1 id="§-28-2-302-2-recreational-license-fee-cooperative-program" tabindex="-1">§ 28.2-302.2 Recreational license fee; cooperative program. <a class="header-anchor" href="#§-28-2-302-2-recreational-license-fee-cooperative-program" aria-label="Permalink to &quot;§ 28.2-302.2 Recreational license fee; cooperative program.&quot;">​</a></h1><p>A. The annual fee for the saltwater recreational fishing license shall be seven dollars and fifty cents or as subsequently revised by the Commission pursuant to § <a href="/vacode/28.2-201/">28.2-201</a>. Agents of the Commission shall retain the agent&#39;s fee established by the Board of Wildlife Resources pursuant to subsection B of § <a href="/vacode/29.1-327/">29.1-327</a>, except that the agent&#39;s fee shall be deducted from the license fee established by the Commission pursuant to subdivision 4 of § <a href="/vacode/28.2-201/">28.2-201</a>, as compensation for issuing each license.</p><p>B. All funds collected under this section shall be paid into the state treasury to the credit of the Virginia Saltwater Recreational Fishing Development Fund, as established in § <a href="/vacode/28.2-302.3/">28.2-302.3</a>.</p><p>C. The Commission shall enter into cooperative programs with the Department of Wildlife Resources as are necessary to carry out the provisions of this section.</p><p>D. The Commission shall also have the power necessary to conduct and establish cooperative fish projects with the federal government as prescribed by Congress and in compliance with rules and regulations promulgated by the United States Secretary of the Interior.</p><p>E. A resident may purchase a license issued under this section that is valid for one year or for multiple years from the date of purchase.</p><p>1992, c. 895; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0193" target="_blank" rel="noreferrer">193</a>.</p>',7),s=[o];function n(l,c,h,p,f,d){return a(),t("div",null,s)}const m=e(i,[["render",n]]);export{u as __pageData,m as default};
