import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 28.2-302.5 Exemptions to saltwater recreational fishing license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.5.md","filePath":"vacode/28.2-302.5.md"}'),t={name:"vacode/28.2-302.5.md"},n=r('<h1 id="§-28-2-302-5-exemptions-to-saltwater-recreational-fishing-license" tabindex="-1">§ 28.2-302.5 Exemptions to saltwater recreational fishing license. <a class="header-anchor" href="#§-28-2-302-5-exemptions-to-saltwater-recreational-fishing-license" aria-label="Permalink to &quot;§ 28.2-302.5 Exemptions to saltwater recreational fishing license.&quot;">​</a></h1><p>A. The following persons shall be exempt from the requirements of obtaining a saltwater recreational fishing license as set forth in § <a href="/vacode/28.2-302.1/">28.2-302.1</a>:</p><ol><li><p>A person under the age of 16 or a person who has attained the age of 65.</p></li><li><p>A person fishing from private real property that he owns or rents, the nonpaying guest of such person, or a member of the immediate family of such person.</p></li><li><p>A person fishing from a licensed recreational boat licensed pursuant to § <a href="/vacode/28.2-302.7/">28.2-302.7</a>.</p></li><li><p>A person fishing from a licensed headboat, charterboat, or pier licensed pursuant to § 28.2-302 or <a href="/vacode/28.2-302.8/">28.2-302.8</a>.</p></li><li><p>A person fishing with gear licensed by the Commission.</p></li><li><p>The holder of a valid recreational fishing license issued by another state or jurisdiction, upon determination of reciprocity of the license by the Commissioner.</p></li><li><p>Members of the following groups, as determined by the Commissioner:</p></li></ol><p>a. Organized groups of individuals with physical or mental limitations;</p><p>b. Organized groups of military veterans residing in veterans&#39; hospitals; and</p><p>c. School groups, grades kindergarten through 12, participating in school-sponsored trips.</p><ol start="8"><li><p>A permanently and totally disabled person as defined in § <a href="/vacode/58.1-3217/">58.1-3217</a> holding a special lifetime saltwater recreational fishing license issued pursuant to § <a href="/vacode/28.2-302.10/">28.2-302.10</a>.</p></li><li><p>A person holding a lifetime saltwater recreational fishing license issued pursuant to § <a href="/vacode/28.2-302.10:1/">28.2-302.10:1</a>.</p></li><li><p>A person fishing from a federally owned park or reserve with boundaries extending into an adjoining state that does not require a saltwater fishing license.</p></li><li><p>A Virginia resident who is a member of an American Indian tribe recognized by the Commonwealth and is carrying (i) an identification card or paper signed by the chief of his tribe, (ii) a valid tribal identification card, (iii) a written confirmation through a central tribal registry, or (iv) a certification from a tribal office, stating that the person is a member of such tribe. Such card or other certification shall create a presumption of residence in Virginia that may be rebutted by proof of actual residence elsewhere.</p></li></ol><p>B. No saltwater recreational fishing licenses shall be required on days that are designated as free fishing days. The Commissioner shall designate no more than three free fishing days in any calendar year. This exemption shall not apply to headboats, charterboats, or rental boats.</p><p>1992, c. 895; 1993, c. 241; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0107" target="_blank" rel="noreferrer">107</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0110" target="_blank" rel="noreferrer">110</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0215" target="_blank" rel="noreferrer">215</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0118" target="_blank" rel="noreferrer">118</a>.</p>',9),o=[n];function s(l,h,c,p,f,d){return i(),a("div",null,o)}const u=e(t,[["render",s]]);export{m as __pageData,u as default};
