import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 28.2-302.7 Recreational boat; reporting of catch.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.7.md","filePath":"vacode/28.2-302.7.md"}'),i={name:"vacode/28.2-302.7.md"},o=t('<h1 id="§-28-2-302-7-recreational-boat-reporting-of-catch" tabindex="-1">§ 28.2-302.7 Recreational boat; reporting of catch. <a class="header-anchor" href="#§-28-2-302-7-recreational-boat-reporting-of-catch" aria-label="Permalink to &quot;§ 28.2-302.7 Recreational boat; reporting of catch.&quot;">​</a></h1><p>A. The owner or operator of a recreational boat used for saltwater recreational fishing shall have the option of purchasing a saltwater recreational fishing license which covers his passengers and himself to meet the licensing requirements set forth in § <a href="http://law.lis.virginia.gov/vacode/28.2-302.1/" target="_blank" rel="noreferrer">28.2-302.1</a>. The cost of the license shall be $30 or as subsequently revised by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a> a year for boats; however, notwithstanding subdivision 4 of § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>, the Commission may institute a one-time fee increase of no more than $10 to purchase the license issued pursuant to this section. If the owner or operator of the boat does not purchase a license which covers all his passengers, individuals shall still purchase a license as required by § <a href="http://law.lis.virginia.gov/vacode/28.2-302.1/" target="_blank" rel="noreferrer">28.2-302.1</a>. Purchasers of licenses pursuant to this section will be issued an individual license, at no additional cost, as required in § <a href="http://law.lis.virginia.gov/vacode/28.2-302.1/" target="_blank" rel="noreferrer">28.2-302.1</a>.</p><p>B. Holders of licenses issued pursuant to subsection A shall report catch and other data as are deemed necessary by the Commission for effective fisheries management.</p><p>C. All funds collected pursuant to this section shall be paid into the state treasury to the credit of the Virginia Saltwater Recreational Fishing Development Fund, as established by § <a href="http://law.lis.virginia.gov/vacode/28.2-302.3/" target="_blank" rel="noreferrer">28.2-302.3</a>.</p><p>1992, c. 895; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0106" target="_blank" rel="noreferrer">106</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0484" target="_blank" rel="noreferrer">484</a>.</p>',5),n=[o];function s(l,c,h,f,p,g){return a(),r("div",null,n)}const v=e(i,[["render",s]]);export{u as __pageData,v as default};
