import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 28.2-302.7:1 Reissuance of licenses for recreational boats.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.7:1.md","filePath":"vacode/28.2-302.7:1.md"}'),t={name:"vacode/28.2-302.7:1.md"},i=o('<h1 id="§-28-2-302-7-1-reissuance-of-licenses-for-recreational-boats" tabindex="-1">§ 28.2-302.7:1 Reissuance of licenses for recreational boats. <a class="header-anchor" href="#§-28-2-302-7-1-reissuance-of-licenses-for-recreational-boats" aria-label="Permalink to &quot;§ 28.2-302.7:1 Reissuance of licenses for recreational boats.&quot;">​</a></h1><p>The Commission shall provide for the reissuance of a saltwater recreational fishing license to a person who holds a valid license for a recreational boat issued pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-302.7/" target="_blank" rel="noreferrer">28.2-302.7</a> and who owns or operates another recreational boat. The cost of reissuing a license shall be five dollars or as subsequently revised by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>. A reissued license shall be valid for the balance of the term of the original license.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0163" target="_blank" rel="noreferrer">163</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0106" target="_blank" rel="noreferrer">106</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',3),s=[i];function n(l,c,f,h,_,d){return a(),r("div",null,s)}const u=e(t,[["render",n]]);export{p as __pageData,u as default};
