import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 28.2-302.9 Commercial fishing pier.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.9.md","filePath":"vacode/28.2-302.9.md"}'),t={name:"vacode/28.2-302.9.md"},s=i('<h1 id="§-28-2-302-9-commercial-fishing-pier" tabindex="-1">§ 28.2-302.9 Commercial fishing pier. <a class="header-anchor" href="#§-28-2-302-9-commercial-fishing-pier" aria-label="Permalink to &quot;§ 28.2-302.9 Commercial fishing pier.&quot;">​</a></h1><p>A. The owner of a commercial fishing pier, as defined in § <a href="http://law.lis.virginia.gov/vacode/28.2-302/" target="_blank" rel="noreferrer">28.2-302</a>, shall have the option of purchasing a saltwater recreational fishing license, covering his customers, in order to meet the licensing requirements set forth in § <a href="http://law.lis.virginia.gov/vacode/28.2-302.1/" target="_blank" rel="noreferrer">28.2-302.1</a>. The cost of the license shall be $450. If the owner of the pier does not purchase such a license, individuals shall still purchase a license as required by § <a href="http://law.lis.virginia.gov/vacode/28.2-302.1/" target="_blank" rel="noreferrer">28.2-302.1</a>.</p><p>B. Owners of commercial piers shall report catch and other data as are deemed necessary by the Commission for effective fisheries management.</p><p>C. All funds collected pursuant to this section shall be paid into the state treasury to the credit of the Virginia Saltwater Recreational Fishing Development Fund, as established by § <a href="http://law.lis.virginia.gov/vacode/28.2-302.3/" target="_blank" rel="noreferrer">28.2-302.3</a>. The Commission may subsequently revise the cost of licenses in this section pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>.</p><p>1992, c. 895; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',5),o=[s];function n(l,c,h,f,p,d){return a(),r("div",null,o)}const g=e(t,[["render",n]]);export{_ as __pageData,g as default};
