import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 28.2-302 Commercial fishing pier; license fee; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-302.md","filePath":"vacode/28.2-302.md"}'),r={name:"vacode/28.2-302.md"},s=o('<h1 id="§-28-2-302-commercial-fishing-pier-license-fee-penalty" tabindex="-1">§ 28.2-302 Commercial fishing pier; license fee; penalty. <a class="header-anchor" href="#§-28-2-302-commercial-fishing-pier-license-fee-penalty" aria-label="Permalink to &quot;§ 28.2-302 Commercial fishing pier; license fee; penalty.&quot;">​</a></h1><p>A. The owner of a commercial fishing pier shall pay an annual license fee of $50 or as subsequently revised by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a> for each pier that is over or upon the subaqueous beds of the Commonwealth.</p><p>B. Net fishing shall not be permitted within 300 yards of the sides or end of a commercial fishing pier. The Commission may decrease this distance if it considers 300 yards to be an excessive distance because of the size of the body of water where the pier is located. A violation of this subsection is a Class 3 misdemeanor.</p><p>C. The construction or erection of a commercial fishing pier on a subaqueous bed of the Commonwealth is subject to the permit requirements of § <a href="http://law.lis.virginia.gov/vacode/28.2-1203/" target="_blank" rel="noreferrer">28.2-1203</a>.</p><p>D. For purposes of this section, a commercial fishing pier means any pier whose primary purpose is to allow fishing by the public for a fee. A public fishing pier operated by a political subdivision shall be considered to be a commercial fishing pier, although no fee is charged.</p><p>1976, c. 343, § 28.1-52.2; 1979, c. 274; 1992, c. 836; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',6),t=[s];function n(c,l,h,p,f,m){return i(),a("div",null,t)}const u=e(r,[["render",n]]);export{_ as __pageData,u as default};
