import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 28.2-400.1 Criteria for qualifying for a limited entry purse seine menhaden bait license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-400.1.md","filePath":"vacode/28.2-400.1.md"}'),r={name:"vacode/28.2-400.1.md"},t=n('<h1 id="§-28-2-400-1-criteria-for-qualifying-for-a-limited-entry-purse-seine-menhaden-bait-license" tabindex="-1">§ 28.2-400.1 Criteria for qualifying for a limited entry purse seine menhaden bait license. <a class="header-anchor" href="#§-28-2-400-1-criteria-for-qualifying-for-a-limited-entry-purse-seine-menhaden-bait-license" aria-label="Permalink to &quot;§ 28.2-400.1 Criteria for qualifying for a limited entry purse seine menhaden bait license.&quot;">​</a></h1><p>A. The Commission shall establish and administer a limited entry purse seine menhaden bait license that meets the requirements of this section.</p><p>B. In order to qualify for a limited entry purse seine menhaden bait license, an applicant shall have held a purse seine license, as established in § <a href="http://law.lis.virginia.gov/vacode/28.2-402/" target="_blank" rel="noreferrer">28.2-402</a>, in 2011 and shall have landed menhaden in the Commonwealth in each of the years 2009, 2010, and 2011. Such person shall also have used purse seine gear to harvest menhaden in at least one of those three years. Proof of landings and gear usage shall be in the form of receipts, landing reports, or other verifiable documents as designated by the Commission.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0059" target="_blank" rel="noreferrer">59</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0760" target="_blank" rel="noreferrer">760</a>.</p>',4),s=[t];function o(l,d,h,c,f,m){return a(),i("div",null,s)}const u=e(r,[["render",o]]);export{_ as __pageData,u as default};
