import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 28.2-502 License fees for taking oysters or clams in tidal waters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-502.md","filePath":"vacode/28.2-502.md"}'),r={name:"vacode/28.2-502.md"},s=o('<h1 id="§-28-2-502-license-fees-for-taking-oysters-or-clams-in-tidal-waters" tabindex="-1">§ 28.2-502 License fees for taking oysters or clams in tidal waters. <a class="header-anchor" href="#§-28-2-502-license-fees-for-taking-oysters-or-clams-in-tidal-waters" aria-label="Permalink to &quot;§ 28.2-502 License fees for taking oysters or clams in tidal waters.&quot;">​</a></h1><p>Every resident who applies for a license to take or catch oysters and clams shall pay as follows or as subsequently revised by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>:</p><ol><li><p>For each person taking or catching oysters by hand or with ordinary tongs, $10;</p></li><li><p>For each single-rigged patent tong boat taking oysters, $35;</p></li><li><p>For each double-rigged patent tong boat taking oysters, $70;</p></li><li><p>For each person taking or catching clams by hand or with ordinary tongs, $15;</p></li><li><p>For each single-rigged patent tong boat taking clams, $35;</p></li><li><p>For each double-rigged patent tong boat taking clams, $70; and</p></li><li><p>For each dredge used for taking or catching clams, $50.</p></li></ol><p>Code 1950, §§ 28-137, 28-157; 1960, c. 517; 1962, c. 406, §§ 28.1-120, 28.1-133; 1964, c. 393; 1966, c. 684; 1970, c. 726; 1979, c. 274; 1991, c. 285; 1992, c. 836; 1993, c. 11; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',4),i=[s];function n(c,l,d,p,g,h){return a(),t("div",null,i)}const m=e(r,[["render",n]]);export{f as __pageData,m as default};
