import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const k=JSON.parse('{"title":"§ 28.2-503 License for taking clams with tongs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-503.md","filePath":"vacode/28.2-503.md"}'),s={name:"vacode/28.2-503.md"},i=e("h1",{id:"§-28-2-503-license-for-taking-clams-with-tongs",tabindex:"-1"},[a("§ 28.2-503 License for taking clams with tongs. "),e("a",{class:"header-anchor",href:"#§-28-2-503-license-for-taking-clams-with-tongs","aria-label":'Permalink to "§ 28.2-503 License for taking clams with tongs."'},"​")],-1),r=e("p",null,`The license for taking clams, by ordinary or patent tongs, shall entitle the holder to take only clams from the public bottoms in the waters of the Commonwealth but not from the public oyster rocks, beds, and shoals or from leased oyster grounds. Such a license does not permit the taking of oysters. The Commission may authorize the taking of clams from the public oyster rocks, beds, and shoals of (i) the lower York River below the patent tong line and (ii) that area in the Poquoson River contained within Public Ground Tract No. 1 and Public Ground Tract No. 9, west of a line connecting York Point and Hunt's Point and northeast of a line connecting Hunt's Point and survey Station "Spit" when it determines that commercial quantities of oysters are not present and are not likely to return due to the presence of oyster diseases. However, the Commission shall annually review the conditions in the area designated under clause (ii) to determine whether to authorize the taking of clams.`,-1),c=e("p",null,"Code 1950, § 28-137; 1960, c. 517; 1962, c. 406, § 28.1-120; 1964, c. 393; 1966, c. 684; 1970, c. 726; 1979, c. 274; 1991, c. 285; 1992, cc. 246, 836; 1993, c. 238.",-1),l=[i,r,c];function h(d,m,u,f,p,g){return o(),n("div",null,l)}const w=t(s,[["render",h]]);export{k as __pageData,w as default};
