import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 28.2-520 Use of hydraulic dredges prohibited; exceptions; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-520.md","filePath":"vacode/28.2-520.md"}'),t={name:"vacode/28.2-520.md"},r=i('<h1 id="§-28-2-520-use-of-hydraulic-dredges-prohibited-exceptions-penalty" tabindex="-1">§ 28.2-520 Use of hydraulic dredges prohibited; exceptions; penalty. <a class="header-anchor" href="#§-28-2-520-use-of-hydraulic-dredges-prohibited-exceptions-penalty" aria-label="Permalink to &quot;§ 28.2-520 Use of hydraulic dredges prohibited; exceptions; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person, unless he has been issued a permit by the Commission pursuant to subsection B, to (i) take or catch clams through the use of a hydraulic dredge or (ii) have on board his boat a hydraulic dredge designed for harvesting seafood.</p><p>B. The Commission may issue a permit authorizing the possession and use of a (i) hydraulic dredge for the purpose of (a) transiting state waters or (b) conducting legitimate aquaculture research and (ii) handheld hydraulically operated device for harvesting cultured clams from leased grounds.</p><p>C. Notwithstanding the provisions of clause (ii) of subsection A, a person may have on board his boat a hydraulic dredge designed for harvesting seafood when traveling to or from docks for maintenance or repair of the boat or equipment, or when off-loading catches made in federal waters.</p><p>D. The Commission and the Virginia Institute of Marine Science may possess and use hydraulic dredges to take and catch shellfish on an experimental basis.</p><p>E. Any person who violates this section is guilty of a Class 1 misdemeanor.</p><p>1981, c. 138, § 28.1-128.01; 1992, c. 836; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0377" target="_blank" rel="noreferrer">377</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0675" target="_blank" rel="noreferrer">675</a>.</p>',7),s=[r];function n(d,c,h,l,p,u){return a(),o("div",null,s)}const m=e(t,[["render",n]]);export{g as __pageData,m as default};
