import{_ as o,o as t,c as s,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 28.2-624 Public rocks, etc., not to be used or staked off; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-624.md","filePath":"vacode/28.2-624.md"}'),r={name:"vacode/28.2-624.md"},n=e("h1",{id:"§-28-2-624-public-rocks-etc-not-to-be-used-or-staked-off-penalty",tabindex:"-1"},[a("§ 28.2-624 Public rocks, etc., not to be used or staked off; penalty. "),e("a",{class:"header-anchor",href:"#§-28-2-624-public-rocks-etc-not-to-be-used-or-staked-off-penalty","aria-label":'Permalink to "§ 28.2-624 Public rocks, etc., not to be used or staked off; penalty."'},"​")],-1),c=e("p",null,"It is unlawful for any person to stake, use, or occupy for the purpose of propagating or planting oysters or shells any public oyster bed, rock, or shoal, or any bottom which has not been assigned to him, or any public clamming grounds which have been set aside as such. The officer for that district or any other officer of the Commission shall require any such person to remove all stakes, watchhouses, or other obstructions from the public beds, rocks, or shoals or from any bottom which has not been assigned to him. The failure of any person to remove such stakes or other obstructions within ten days of receiving the written notice is a separate and additional unlawful act and violation of this section. The stakes or other obstructions shall be removed by the officer at the cost of the person unlawfully placing or having placed the stakes or other obstructions.",-1),i=e("p",null,"A violation of this section is a Class 3 misdemeanor.",-1),l=e("p",null,"Code 1950, § 28-125; 1962, c. 406, § 28.1-111; 1992, c. 836.",-1),h=[n,c,i,l];function d(f,u,p,b,m,_){return t(),s("div",null,h)}const v=o(r,[["render",d]]);export{y as __pageData,v as default};
