import{_ as t,o as r,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 28.2-627.1 Oyster Leasing, Conservation, and Repletion Programs Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-627.1.md","filePath":"vacode/28.2-627.1.md"}'),s={name:"vacode/28.2-627.1.md"},o=e("h1",{id:"§-28-2-627-1-oyster-leasing-conservation-and-repletion-programs-fund",tabindex:"-1"},[n("§ 28.2-627.1 Oyster Leasing, Conservation, and Repletion Programs Fund. "),e("a",{class:"header-anchor",href:"#§-28-2-627-1-oyster-leasing-conservation-and-repletion-programs-fund","aria-label":'Permalink to "§ 28.2-627.1 Oyster Leasing, Conservation, and Repletion Programs Fund."'},"​")],-1),i=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Oyster Leasing, Conservation, and Repletion Programs Fund, referred to in this section as "the Fund." The Fund shall be established on the books of the Comptroller. All oyster planting ground application fees, oyster planting ground transfer fees, oyster planting ground lease renewal fees, and oyster ground rents collected pursuant to this chapter shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for the purposes of administering the oyster ground leasing program and the conservation and repletion program. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Commissioner.',-1),d=e("p",null,[n("2020, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0806",target:"_blank",rel:"noreferrer"},"806"),n(".")],-1),l=[o,i,d];functio