import{_ as o,o as a,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 28.2-632 Public clamming grounds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-632.md","filePath":"vacode/28.2-632.md"}'),s={name:"vacode/28.2-632.md"},i=e("h1",{id:"§-28-2-632-public-clamming-grounds",tabindex:"-1"},[t("§ 28.2-632 Public clamming grounds. "),e("a",{class:"header-anchor",href:"#§-28-2-632-public-clamming-grounds","aria-label":'Permalink to "§ 28.2-632 Public clamming grounds."'},"​")],-1),r=e("p",null,"Any ground in the waters of this Commonwealth not assigned to anyone for planting or bathing purposes may be, on application of twenty or more citizens to the officer assigned to the district in which the land lies, laid off and designated as public clamming grounds; or the Commissioner may do so without such petition, provided in his opinion no oyster interests will suffer thereby and the clams are of sufficient quantity for a person to realize at least 225 clams or $1.50 per day catching and taking clams from such ground. If the ground is laid off, the Commissioner shall designate by stakes the metes and bounds of such ground and also have a plat made, to be recorded in the clerk's office of the county where the ground lies. All costs of surveying, platting, and recording shall be paid by the applicant. Such ground shall be set apart and remain a public clamming ground for the common use of the citizens of Virginia for so long as the Commissioner determines, and shall not be assigned to anyone during such period.",-1),c=e("p",null,"Code 1950, § 28-180; 1962, c. 406, § 28.1-162; 1992, c. 836.",-1),d=[i,r,c];function l(h,m,u,g,f,p){return a(),n("div",null,d)}const y=o(s,[["render",l]]);export{b as __pageData,y as default};
