import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 28.2-702 Licenses to take crabs; shedding operations; amount of fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-702.md","filePath":"vacode/28.2-702.md"}'),t={name:"vacode/28.2-702.md"},i=a('<h1 id="§-28-2-702-licenses-to-take-crabs-shedding-operations-amount-of-fee" tabindex="-1">§ 28.2-702 Licenses to take crabs; shedding operations; amount of fee. <a class="header-anchor" href="#§-28-2-702-licenses-to-take-crabs-shedding-operations-amount-of-fee" aria-label="Permalink to &quot;§ 28.2-702 Licenses to take crabs; shedding operations; amount of fee.&quot;">​</a></h1><p>Any person desiring to take or catch crabs for market or profit from the waters of this Commonwealth, or waters under its jurisdiction, or any person desiring to engage in the business of buying or marketing crabs for packing or canning crabs, shall pay to any officer the following fees or as such fees may be subsequently revised by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/28.2-201/" target="_blank" rel="noreferrer">28.2-201</a>:</p><ol><li><p>For each person taking or catching crabs by dip nets, $8;</p></li><li><p>For ordinary trotlines, $8;</p></li><li><p>For patent trotlines, $31;</p></li><li><p>For up to 100 crab pots, $29;</p></li><li><p>For over 100 but not more than 300 crab pots, $48;</p></li><li><p>For over 300 but not more than 500 crab pots, $100;</p></li><li><p>For over 500 crab pots, $250;</p></li><li><p>For each boat used for taking or catching hard crabs with dredges, $58;</p></li><li><p>For each crab trap or crab pound, $5;</p></li><li><p>For each single-rigged crab-scrape boat, $16;</p></li><li><p>For each double-rigged crab-scrape boat, $32;</p></li><li><p>For up to 20 tanks and floats for shedding crabs, $7.50;</p></li><li><p>For more than 20 tanks or floats for shedding crabs, $15; and</p></li><li><p>For taking or catching peeler crabs using peeler pots, $29.</p></li></ol><p>Code 1950, § 28-170; 1954, c. 368; 1956, c. 293; 1960, c. 517; 1962, c. 406, § 28.1-165; 1964, c. 393; 1966, c. 684; 1968, c. 785; 1970, c. 726; 1979, c. 274; 1983, cc. 307, 603; 1985, c. 180; 1990, c. 154; 1992, c. 836; 1993, c. 11; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',4),s=[i];function n(c,p,l,d,h,f){return o(),r("div",null,s)}const _=e(t,[["render",n]]);export{g as __pageData,_ as default};
