import{_ as e,o,c as s,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 28.2-816 Supervision of removal, relaying or depuration; seasons established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-816.md","filePath":"vacode/28.2-816.md"}'),r={name:"vacode/28.2-816.md"},t=a('<h1 id="§-28-2-816-supervision-of-removal-relaying-or-depuration-seasons-established" tabindex="-1">§ 28.2-816 Supervision of removal, relaying or depuration; seasons established. <a class="header-anchor" href="#§-28-2-816-supervision-of-removal-relaying-or-depuration-seasons-established" aria-label="Permalink to &quot;§ 28.2-816 Supervision of removal, relaying or depuration; seasons established.&quot;">​</a></h1><p>A. Shellfish removal or relaying from condemned areas shall be under the supervision of the Commissioner of Marine Resources and the State Health Commissioner.</p><p>B. The season for the removal or relaying of shellfish shall be:</p><ol><li><p>April 1 to November 1 from private grounds.</p></li><li><p>May 1 to August 15 from public grounds.</p></li></ol><p>The dates for the opening and closing of seasons may be changed by the Marine Resources Commission, and the Marine Resources Commission may refuse to grant permits for removal of shellfish from condemned areas of the waters of the Commonwealth.</p><p>C. The Marine Resources Commission, after consultation with the Department of Health, shall establish the season for removal of shellfish for depuration from certain condemned areas designated by the State Health Commissioner.</p><p>Code 1950, § 28-162.1; 1960, c. 517; 1962, c. 406, § 28.1-179; 1966, c. 684; 1968, c. 745; 1979, c. 274; 1981, c. 52; 1986, c. 184; 1988, c. 600; 1992, c. 836.</p>',7),i=[t];function n(l,h,d,m,c,p){return o(),s("div",null,i)}const _=e(r,[["render",n]]);export{u as __pageData,_ as default};
