import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 28.2-821 Violations; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/28.2-821.md","filePath":"vacode/28.2-821.md"}'),i={name:"vacode/28.2-821.md"},r=t('<h1 id="§-28-2-821-violations-penalty" tabindex="-1">§ 28.2-821 Violations; penalty. <a class="header-anchor" href="#§-28-2-821-violations-penalty" aria-label="Permalink to &quot;§ 28.2-821 Violations; penalty.&quot;">​</a></h1><p>A. It is unlawful for any person to have in his possession, to store, to sell, or to offer for sale any shellfish which have been removed or taken from a condemned area other than as provided in § <a href="http://law.lis.virginia.gov/vacode/28.2-810/" target="_blank" rel="noreferrer">28.2-810</a>. Any person who violates this section or any provision of this chapter is guilty of a Class 1 misdemeanor.</p><p>B. Upon conviction of violating any provision of this chapter any boat, vessel, motor vehicle or equipment used in committing the violation may be forfeited as provided by Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2.</p><p>Code 1950, § 28-165; 1962, c. 406, § 28.1-181; 1992, c. 836; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',4),n=[r];function s(l,c,p,h,f,_){return a(),o("div",null,n)}const g=e(i,[["render",s]]);export{v as __pageData,g as default};
