import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 29.1-108 Audits and examinations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-108.md","filePath":"vacode/29.1-108.md"}'),n={name:"vacode/29.1-108.md"},o=r('<h1 id="§-29-1-108-audits-and-examinations" tabindex="-1">§ 29.1-108 Audits and examinations. <a class="header-anchor" href="#§-29-1-108-audits-and-examinations" aria-label="Permalink to &quot;§ 29.1-108 Audits and examinations.&quot;">​</a></h1><p>The minute books and other records of the Board shall be open to examination by the Governor, members of the General Assembly, and Auditor of Public Accounts, or their representatives, at all times. The accounts of the Board shall be audited in the manner provided for the audit of other state agencies. In addition, the Board shall ensure that the Auditor of Public Accounts, or an entity approved by him, conducts an audit of a fiscal and compliance nature of the accounts and transactions of the Department as determined necessary by the Auditor of Public Accounts. The Board may order such other audits as it deems necessary and desirable.</p><p>Code 1950, § 29-18; 1987, c. 488; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0915" target="_blank" rel="noreferrer">915</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0307" target="_blank" rel="noreferrer">307</a>.</p>',3),i=[o];function s(d,c,l,h,u,f){return a(),t("div",null,i)}const m=e(n,[["render",s]]);export{p as __pageData,m as default};
