import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 29.1-300.1 Certification of competence in hunter education; incentives.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-300.1.md","filePath":"vacode/29.1-300.1.md"}'),a={name:"vacode/29.1-300.1.md"},o=n('<h1 id="§-29-1-300-1-certification-of-competence-in-hunter-education-incentives" tabindex="-1">§ 29.1-300.1 Certification of competence in hunter education; incentives. <a class="header-anchor" href="#§-29-1-300-1-certification-of-competence-in-hunter-education-incentives" aria-label="Permalink to &quot;§ 29.1-300.1 Certification of competence in hunter education; incentives.&quot;">​</a></h1><p>A. Except as provided in subsection B and §§ <a href="/vacode/29.1-300.4/">29.1-300.4</a> and <a href="/vacode/29.1-305.2/">29.1-305.2</a>, no hunting license shall be issued to (i) a person who has never obtained a license to hunt in any state or country, or (ii) a person who is under the age of 16, unless such a person presents to the Board or one of its authorized license vendors, a certificate of completion in hunter education issued or authorized by the Board under the hunter education program, or proof that he holds the equivalent certificate obtained from an authorized agency or association of another state or country.</p><p>B. Although a resident under the age of 12 is not required to obtain a license to hunt, any person under the age of 12, or an individual on his behalf, may purchase a Virginia hunting license or a junior lifetime hunting license pursuant to § <a href="/vacode/29.1-302.1/">29.1-302.1</a>, without completing a hunter education program as required in subsection A, provided that no person under the age of 12 shall hunt unless accompanied and directly supervised by an adult who has, on his person, a valid Virginia hunting license. The junior lifetime hunting license issued to an individual under the age of 12 shall become invalid on the individual&#39;s twelfth birthday and remain invalid until certification of competence in hunter education is shown as provided in this section. A lifetime license, indicating the completion of hunter education or an equivalent certificate, shall be reissued at no cost when such proof is provided.</p><p>The adult shall be responsible for such supervision. For the purposes of this section, &quot;adult&quot; means the parent or legal guardian of the person under age 12, or such person over the age of 18 designated by the parent or legal guardian.</p><p>&quot;Accompanied and directly supervised&quot; means that the adult is within sight of the person under the age of 12.</p><p>C. This section shall not apply to persons while on horseback hunting foxes with hounds but without firearms.</p><p>D. The Board may adopt regulations that provide incentives for successful completion of a hunter education course to hunters who are not required by law to complete such a course. The regulations may include such incentives as the Board deems appropriate.</p><p>1987, c. 83, § 29-51.1; 1988, cc. 179, 384, 474; 1989, c. 204; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0118" target="_blank" rel="noreferrer">118</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0247" target="_blank" rel="noreferrer">247</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0018" target="_blank" rel="noreferrer">18</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0416" target="_blank" rel="noreferrer">416</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0798" target="_blank" rel="noreferrer">798</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0123" target="_blank" rel="noreferrer">123</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',8),r=[o];function s(c,h,l,u,d,p){return i(),t("div",null,r)}const v=e(a,[["render",s]]);export{g as __pageData,v as default};
