import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-302.03 Special licenses for certain nonresident disabled veterans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-302.03.md","filePath":"vacode/29.1-302.03.md"}'),r={name:"vacode/29.1-302.03.md"},i=n('<h1 id="§-29-1-302-03-special-licenses-for-certain-nonresident-disabled-veterans" tabindex="-1">§ 29.1-302.03 Special licenses for certain nonresident disabled veterans. <a class="header-anchor" href="#§-29-1-302-03-special-licenses-for-certain-nonresident-disabled-veterans" aria-label="Permalink to &quot;§ 29.1-302.03 Special licenses for certain nonresident disabled veterans.&quot;">​</a></h1><p>A. Any nonresident veteran who is totally and permanently disabled due to a service-connected disability as certified by the U.S. Department of Veterans Affairs shall pay an amount equal to one-quarter the fee for the state nonresident hunting license required by subdivision 3 of § <a href="http://law.lis.virginia.gov/vacode/29.1-303/" target="_blank" rel="noreferrer">29.1-303</a>. The license fees established by this subsection may be revised by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-103/" target="_blank" rel="noreferrer">29.1-103</a>.</p><p>B. Any nonresident veteran who is totally and permanently disabled due to a service-connected disability as certified by the U.S. Department of Veterans Affairs shall pay an amount equal to one-quarter the fee for the state nonresident fishing license required by subdivision A 3 of § <a href="http://law.lis.virginia.gov/vacode/29.1-310/" target="_blank" rel="noreferrer">29.1-310</a>. However, this license shall not entitle such nonresident veteran to fish in designated waters stocked with trout by the Department or other public body. The license fees established by this subsection may be revised by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-103/" target="_blank" rel="noreferrer">29.1-103</a>.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>.</p>',4),s=[i];function o(l,d,c,h,f,p){return t(),a("div",null,s)}const v=e(r,[["render",o]]);export{_ as __pageData,v as default};
