import{_ as e,o as a,c as i,O as s}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-302.3 Special guest fishing licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-302.3.md","filePath":"vacode/29.1-302.3.md"}'),t={name:"vacode/29.1-302.3.md"},n=s('<h1 id="§-29-1-302-3-special-guest-fishing-licenses" tabindex="-1">§ 29.1-302.3 Special guest fishing licenses. <a class="header-anchor" href="#§-29-1-302-3-special-guest-fishing-licenses" aria-label="Permalink to &quot;§ 29.1-302.3 Special guest fishing licenses.&quot;">​</a></h1><p>An owner or bona fide lessee of private land bordering inland waters lying adjacent to North Carolina land or water may apply for a special guest fishing license entitling the licensee and his guests to fish from the property and any private dock, pier or other permanent extension into public waters without an additional fishing license except as required in designated waters stocked with trout and in waters where a daily fishing fee has been imposed pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-318/" target="_blank" rel="noreferrer">29.1-318</a>. The annual fee of a special guest fishing license shall be $50 or as subsequently revised by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-103/" target="_blank" rel="noreferrer">29.1-103</a>. A special guest fishing license shall not be valid for any property, pier or dock operated for any commercial purpose. A special guest fishing license shall not be in force unless displayed on the premises of the property. A special guest fishing license shall not be transferable.</p><p>1992, c. 198; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',3),r=[n];function o(l,c,p,h,d,f){return a(),i("div",null,r)}const u=e(t,[["render",o]]);export{_ as __pageData,u as default};
