import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-302 Special license for certain resident disabled veterans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-302.md","filePath":"vacode/29.1-302.md"}'),n={name:"vacode/29.1-302.md"},i=a('<h1 id="§-29-1-302-special-license-for-certain-resident-disabled-veterans" tabindex="-1">§ 29.1-302 Special license for certain resident disabled veterans. <a class="header-anchor" href="#§-29-1-302-special-license-for-certain-resident-disabled-veterans" aria-label="Permalink to &quot;§ 29.1-302 Special license for certain resident disabled veterans.&quot;">​</a></h1><p>A. Certain resident veterans who are disabled due to a service-connected disability may apply for and receive from the Department a nontransferable license pursuant to 4VAC15-20-65, valid for life, permitting the veteran to hunt and freshwater fish, or to hunt only or to freshwater fish only, depending on which license is purchased, on any property in the Commonwealth according to restrictions and regulations of law. However, this license shall not entitle the owner to fish in designated waters stocked with trout by the Department or other public body.</p><p>B. The cost for a license under this section shall be:</p><ol><li><p>For a resident veteran rated as totally and permanently disabled by the U.S. Department of Veterans Affairs, no cost;</p></li><li><p>For a resident veteran rated 70 percent or more disabled, but less than totally and permanently disabled, by the U.S. Department of Veterans Affairs, $50;</p></li><li><p>For a resident veteran rated 50 percent or more disabled, but less than 70 percent disabled, by the U.S. Department of Veterans Affairs, $75; and</p></li><li><p>For a resident veteran rated 30 percent or more disabled, but less than 50 percent disabled, by the U.S. Department of Veterans Affairs, $100.</p></li></ol><p>1976, c. 234, § 29-52.2; 1977, c. 167; 1985, c. 65; 1987, c. 488; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0321" target="_blank" rel="noreferrer">321</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0380" target="_blank" rel="noreferrer">380</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0040" target="_blank" rel="noreferrer">40</a>.</p>',5),s=[i];function o(l,d,c,p,f,h){return t(),r("div",null,s)}const g=e(n,[["render",o]]);export{_ as __pageData,g as default};
