import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-305.2 Special fox hunting licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-305.2.md","filePath":"vacode/29.1-305.2.md"}'),i={name:"vacode/29.1-305.2.md"},n=t('<h1 id="§-29-1-305-2-special-fox-hunting-licenses" tabindex="-1">§ 29.1-305.2 Special fox hunting licenses. <a class="header-anchor" href="#§-29-1-305-2-special-fox-hunting-licenses" aria-label="Permalink to &quot;§ 29.1-305.2 Special fox hunting licenses.&quot;">​</a></h1><p>There shall be a special license for hunting foxes on horseback with hounds but without firearms. The special license shall exempt the licensee from the hunter education requirement of § <a href="http://law.lis.virginia.gov/vacode/29.1-300.1/" target="_blank" rel="noreferrer">29.1-300.1</a>. The fee for the special license shall be the same as any license required by § <a href="http://law.lis.virginia.gov/vacode/29.1-303/" target="_blank" rel="noreferrer">29.1-303</a> and shall be subject to subsequent revision by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-103/" target="_blank" rel="noreferrer">29.1-103</a>. This special license shall not be required of any person holding a hunting license required by § <a href="http://law.lis.virginia.gov/vacode/29.1-303/" target="_blank" rel="noreferrer">29.1-303</a>.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0798" target="_blank" rel="noreferrer">798</a>.</p>',3),s=[n];function l(o,c,h,_,f,p){return a(),r("div",null,s)}const g=e(i,[["render",l]]);export{u as __pageData,g as default};
