import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 29.1-305 Special license for hunting bear, deer and turkey; authority of Board to create bear license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-305.md","filePath":"vacode/29.1-305.md"}'),n={name:"vacode/29.1-305.md"},o=t('<h1 id="§-29-1-305-special-license-for-hunting-bear-deer-and-turkey-authority-of-board-to-create-bear-license" tabindex="-1">§ 29.1-305 Special license for hunting bear, deer and turkey; authority of Board to create bear license. <a class="header-anchor" href="#§-29-1-305-special-license-for-hunting-bear-deer-and-turkey-authority-of-board-to-create-bear-license" aria-label="Permalink to &quot;§ 29.1-305 Special license for hunting bear, deer and turkey; authority of Board to create bear license.&quot;">​</a></h1><p>A. A special license is required for hunting bear, deer and turkey in this Commonwealth, which shall be in addition to the license required to hunt other game. The fee for the special license shall be $12 for a resident age 16 or older, $7.50 for a resident under the age of 16, and $60 for a nonresident 16 years of age or older, $15 for a nonresident 12 years of age to 15 years of age, and $12 for a nonresident younger than 12 years of age.</p><p>B. The Board may create a separate special license for the hunting of bear in this Commonwealth. The fee for such a special license shall be $25 for residents and $150 for nonresidents. A person who obtains a special license for hunting bear shall also be required to obtain the state resident license or state nonresident license pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-303/" target="_blank" rel="noreferrer">29.1-303</a>. If a special license to hunt bear is established by the Board, the special license required in subsection A shall authorize the hunting of deer and turkey only.</p><p>The license to hunt bear, deer and turkey or, if authorized by the Board, the license to hunt bear may be obtained from the clerk or agent of any county or city whose duty it is to sell hunting licenses.</p><p>C. The Board may subsequently revise the cost of licenses set forth in this section pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-103/" target="_blank" rel="noreferrer">29.1-103</a>.</p><p>Code 1950, § 29-122; 1958, c. 318; 1960, c. 568; 1966, c. 493; 1972, c. 509; 1974, c. 363; 1980, c. 494; 1987, c. 488; 1988, c. 250; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>.</p>',6),i=[o];function s(l,c,h,d,f,u){return r(),a("div",null,i)}const g=e(n,[["render",s]]);export{b as __pageData,g as default};
