import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 29.1-307 Special muzzleloading license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-307.md","filePath":"vacode/29.1-307.md"}'),l={name:"vacode/29.1-307.md"},n=t('<h1 id="§-29-1-307-special-muzzleloading-license" tabindex="-1">§ 29.1-307 Special muzzleloading license. <a class="header-anchor" href="#§-29-1-307-special-muzzleloading-license" aria-label="Permalink to &quot;§ 29.1-307 Special muzzleloading license.&quot;">​</a></h1><p>There shall be a license for hunting with a muzzleloader or arrowgun during the special muzzleloading seasons, which shall be in addition to the license required to hunt small game.</p><p>The fee for the special license shall be $12 for a resident and $25 for a nonresident. The special muzzleloader license may be obtained from the clerk or agent whose duty it is to sell licenses in any county or city. The Board may subsequently revise the cost of licenses set forth in this section pursuant to § <a href="/vacode/29.1-103/">29.1-103</a>.</p><p>1990, c. 115; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0557" target="_blank" rel="noreferrer">557</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0558" target="_blank" rel="noreferrer">558</a>.</p>',4),r=[n];function o(s,c,d,h,p,_){return a(),i("div",null,r)}const g=e(l,[["render",o]]);export{f as __pageData,g as default};
