import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-317 Special fishing permits for certain juveniles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-317.md","filePath":"vacode/29.1-317.md"}'),a={name:"vacode/29.1-317.md"},n=r('<h1 id="§-29-1-317-special-fishing-permits-for-certain-juveniles" tabindex="-1">§ 29.1-317 Special fishing permits for certain juveniles. <a class="header-anchor" href="#§-29-1-317-special-fishing-permits-for-certain-juveniles" aria-label="Permalink to &quot;§ 29.1-317 Special fishing permits for certain juveniles.&quot;">​</a></h1><p>A. Upon application from the superintendent of any juvenile correctional center maintained and operated by the Department of Juvenile Justice, the Director may issue a permit to allow the residents of such juvenile correctional center to fish under supervision without licenses in public waters open to fishing. The permits shall not be issued for use in designated waters stocked with trout or in waters where a daily fishing fee has been imposed pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-318/" target="_blank" rel="noreferrer">29.1-318</a>.</p><p>B. The application for the permit shall state the name and description of the group, the period of time during which it will be used, the general area in which it will be used, and the name of the person who will be responsible for the group.</p><p>1980, c. 188, § 29-55.3:4; 1987, c. 488; 1989, c. 733; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>.</p>',4),o=[n];function s(l,c,p,h,f,d){return i(),t("div",null,o)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
