import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 29.1-319 Persons entitled to county, city, or state resident licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-319.md","filePath":"vacode/29.1-319.md"}'),n={name:"vacode/29.1-319.md"},r=o('<h1 id="§-29-1-319-persons-entitled-to-county-city-or-state-resident-licenses" tabindex="-1">§ 29.1-319 Persons entitled to county, city, or state resident licenses. <a class="header-anchor" href="#§-29-1-319-persons-entitled-to-county-city-or-state-resident-licenses" aria-label="Permalink to &quot;§ 29.1-319 Persons entitled to county, city, or state resident licenses.&quot;">​</a></h1><p>The following persons shall be entitled to a state resident license or to a county or a city license to hunt, trap, or fish provided that applications for a county or city license shall be made in the county or city in which the person meets the following criteria:</p><ol><li><p>Any person born in the United States or who has been naturalized and who has been a bona fide resident of the county or city for six months next preceding the date of application for license in the county or city;</p></li><li><p>All persons who are and for two or more months next preceding the date of the application have been domiciliary residents of Virginia, upon execution of a certificate of residence prescribed by the Director;</p></li><li><p>Any legal voter of the county or city in which the license is applied for;</p></li><li><p>Any unnaturalized person who owns real estate in the county or city and who has actually resided there not less than five years next preceding the date of the application for the license in the county or city;</p></li><li><p>Any member of the armed forces of the United States, or a member of the immediate family of such a member as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-3101/" target="_blank" rel="noreferrer">2.2-3101</a>, upon execution of a certificate of residence if the member (i) resides in the Commonwealth, (ii) is on active duty, and (iii) is stationed at a military installation within, or in a ship based in, the Commonwealth;</p></li><li><p>Any member of the armed forces of the United States, on active duty, when authorized by the commanding officer of a military reservation. The privileges of this license shall be limited to hunting, trapping, or fishing only within the boundaries of that military reservation;</p></li><li><p>Any student, a resident of the Commonwealth, regularly enrolled in any bona fide preparatory school or institution of higher education in the Commonwealth and any student, not a resident of the Commonwealth, regularly enrolled in and boarding at any such school or institution who presents a certificate of enrollment for the current year to the clerk or any license agent in the county or city in which the school or institution is located;</p></li><li><p>Residents of cities whose limits are wholly within the county where the license is applied for, provided the residents have physically resided within the city for a period of six consecutive months before applying for a license.</p></li></ol><p>Code 1950, §§ 29-57, 29-58; 1956, c. 68; 1960, c. 536; 1962, c. 469; 1964, cc. 160, 451; 1966, c. 527; 1984, c. 26; 1987, c. 488; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0678" target="_blank" rel="noreferrer">678</a>.</p>',4),a=[r];function s(c,l,h,d,p,f){return t(),i("div",null,a)}const u=e(n,[["render",s]]);export{m as __pageData,u as default};
