import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 29.1-328 Term of licenses and permits; multiple-year license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-328.md","filePath":"vacode/29.1-328.md"}'),i={name:"vacode/29.1-328.md"},n=t('<h1 id="§-29-1-328-term-of-licenses-and-permits-multiple-year-license" tabindex="-1">§ 29.1-328 Term of licenses and permits; multiple-year license. <a class="header-anchor" href="#§-29-1-328-term-of-licenses-and-permits-multiple-year-license" aria-label="Permalink to &quot;§ 29.1-328 Term of licenses and permits; multiple-year license.&quot;">​</a></h1><p>A. Hunting and trapping licenses and permits shall be valid for one year from their date of purchase or future effective date, as authorized by the Department, except those licenses issued pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-305/" target="_blank" rel="noreferrer">29.1-305</a>, which shall be valid from July 1 of each year or their later date of purchase, to June 30 of the following year, unless sooner revoked.</p><p>B. Fishing licenses shall run and be valid for one year from their date of purchase or future effective date, as authorized by the Department.</p><p>C. The Board may authorize the sale of any annual license or permit issued under this title for a period exceeding the one-year term. Such a multiple-year license or permit shall be valid for the number of consecutive years stated on the permit or license. The fee for such a multiple-year license or permit shall not exceed the total amount that would be charged if the particular license or permit was purchased on an annual basis over the specified number of years.</p><p>D. The terms of licenses listed in subsections A, B, and C shall not apply to the lifetime hunting and fishing license established in § <a href="http://law.lis.virginia.gov/vacode/29.1-302.1/" target="_blank" rel="noreferrer">29.1-302.1</a>.</p><p>E. National Forest Stamps, issued pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-408/" target="_blank" rel="noreferrer">29.1-408</a>, shall be valid for one year from their date of purchase, unless sooner revoked.</p><p>Code 1950, § 29-66; 1980, c. 494; 1987, cc. 95, 488; 1988, c. 161; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0175" target="_blank" rel="noreferrer">175</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0039" target="_blank" rel="noreferrer">39</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0819" target="_blank" rel="noreferrer">819</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0255" target="_blank" rel="noreferrer">255</a>.</p>',7),l=[n];function s(o,c,h,p,f,d){return r(),a("div",null,l)}const g=e(i,[["render",s]]);export{m as __pageData,g as default};
