import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 29.1-344.1 Stationary duck blind license; riparian landowners exempted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-344.1.md","filePath":"vacode/29.1-344.1.md"}'),n={name:"vacode/29.1-344.1.md"},i=t('<h1 id="§-29-1-344-1-stationary-duck-blind-license-riparian-landowners-exempted" tabindex="-1">§ 29.1-344.1 Stationary duck blind license; riparian landowners exempted. <a class="header-anchor" href="#§-29-1-344-1-stationary-duck-blind-license-riparian-landowners-exempted" aria-label="Permalink to &quot;§ 29.1-344.1 Stationary duck blind license; riparian landowners exempted.&quot;">​</a></h1><p>Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/29.1-340/" target="_blank" rel="noreferrer">29.1-340</a>, the owners of riparian rights or their invitees shall not be required to obtain a stationary blind license when hunting waterfowl from such a blind located on the riparian owner&#39;s property. However, a stationary blind license shall be required in order to afford the riparian owners the protections provided by §§ <a href="http://law.lis.virginia.gov/vacode/29.1-344/" target="_blank" rel="noreferrer">29.1-344</a>, <a href="http://law.lis.virginia.gov/vacode/29.1-345/" target="_blank" rel="noreferrer">29.1-345</a>, and <a href="http://law.lis.virginia.gov/vacode/29.1-349/" target="_blank" rel="noreferrer">29.1-349</a>.</p><p>1992, c. 237; 1993, c. 209.</p>',3),o=[i];function d(l,s,c,p,_,h){return a(),r("div",null,o)}const b=e(n,[["render",d]]);export{v as __pageData,b as default};
