import{_ as e,o as a,c as t,O as s}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-354 Stamps required; issuance; fee; affixing stamps; cancellation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-354.md","filePath":"vacode/29.1-354.md"}'),i={name:"vacode/29.1-354.md"},n=s('<h1 id="§-29-1-354-stamps-required-issuance-fee-affixing-stamps-cancellation" tabindex="-1">§ 29.1-354 Stamps required; issuance; fee; affixing stamps; cancellation. <a class="header-anchor" href="#§-29-1-354-stamps-required-issuance-fee-affixing-stamps-cancellation" aria-label="Permalink to &quot;§ 29.1-354 Stamps required; issuance; fee; affixing stamps; cancellation.&quot;">​</a></h1><p>It shall be unlawful for any person to hunt bear, deer or elk in any locality adopting a damage stamp ordinance within the Commonwealth without having first obtained the special stamp. A violation of this provision shall be punishable as a Class 3 misdemeanor.</p><p>The annual fee for such a stamp shall be $1. The local governing body may prescribe any fee, not to exceed $5 for these special stamps, when issued to nonresidents of the Commonwealth.</p><p>The special stamps shall be obtained from a locally designated official or from any agent designated by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-327/" target="_blank" rel="noreferrer">29.1-327</a>. The agent shall be paid a fee of $.10 from the special fund for each stamp issued.</p><p>The stamp shall be affixed to the reverse side of a current hunting license of each person required to obtain the stamp, and that person shall cancel the stamp with his initials.</p><p>1981, c. 16, § 29-92.3; 1987, c. 488; 1989, c. 421; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0137" target="_blank" rel="noreferrer">137</a>.</p>',6),o=[n];function r(l,c,p,h,f,d){return a(),t("div",null,o)}const _=e(i,[["render",r]]);export{u as __pageData,_ as default};
