import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-408 Permit required; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-408.md","filePath":"vacode/29.1-408.md"}'),o={name:"vacode/29.1-408.md"},r=a('<h1 id="§-29-1-408-permit-required-exceptions" tabindex="-1">§ 29.1-408 Permit required; exceptions. <a class="header-anchor" href="#§-29-1-408-permit-required-exceptions" aria-label="Permalink to &quot;§ 29.1-408 Permit required; exceptions.&quot;">​</a></h1><p>No person shall hunt, fish, or trap on any lands in the national forests in this Commonwealth without first obtaining, in addition to the regular resident or nonresident license, a special permit to hunt, fish, or trap on such areas in the national forests as the Board and the Forest Service may agree upon. However, no such permit shall be required of (i) residents under the age of 16 to fish or trap; (ii) residents over the age of 65 to fish; (iii) nonresidents under the age of 16 to fish when accompanied by a person possessing a valid license to fish therein; (iv) residents possessing a license as provided by subsection E of § <a href="/vacode/29.1-301/">29.1-301</a>; and (v) persons holding a license as provided by § <a href="/vacode/29.1-339/">29.1-339</a>.</p><p>The violation of any of the terms of this article shall constitute a Class 3 misdemeanor.</p><p>Code 1950, § 29-117; 1972, c. 381; 1977, c. 377; 1987, c. 488; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',4),s=[r];function n(d,c,h,p,l,f){return t(),i("div",null,s)}const m=e(o,[["render",n]]);export{u as __pageData,m as default};
