import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-416.1 Use of eel pots in the waters of Back Bay and its tributaries.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-416.1.md","filePath":"vacode/29.1-416.1.md"}'),i={name:"vacode/29.1-416.1.md"},s=r('<h1 id="§-29-1-416-1-use-of-eel-pots-in-the-waters-of-back-bay-and-its-tributaries" tabindex="-1">§ 29.1-416.1 Use of eel pots in the waters of Back Bay and its tributaries. <a class="header-anchor" href="#§-29-1-416-1-use-of-eel-pots-in-the-waters-of-back-bay-and-its-tributaries" aria-label="Permalink to &quot;§ 29.1-416.1 Use of eel pots in the waters of Back Bay and its tributaries.&quot;">​</a></h1><p>A. The Board may permit a licensee to use eel pots to take American eels in the waters of Back Bay and North Landing River and their tributaries for sale or private use.</p><p>B. The annual fee for a permit to set eel pots in the waters of Back Bay and North Landing River and their tributaries for the purpose of taking American eels for private use or for sale shall be as follows:</p><ol><li><p>Eel pots to catch eels for sale, $50; and</p></li><li><p>Eel pots to catch eels for personal use, $17.50.</p></li></ol><p>C. Any nonresident desiring to take or catch American eels in the waters of Back Bay and North Landing River and their tributaries using eel pots shall first pay $350 per year to the Department for a nonresident harvester&#39;s permit, unless he has paid a permit fee pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/29.1-416/" target="_blank" rel="noreferrer">29.1-416</a>. A separate nonresident harvester&#39;s permit shall be required for each boat used to take or harvest American eels in the waters of Back Bay and North Landing River and their tributaries, and shall be in addition to any other permit required for the activity involved.</p><p>2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',6),o=[s];function n(l,p,d,h,c,f){return a(),t("div",null,o)}const b=e(i,[["render",n]]);export{_ as __pageData,b as default};
