import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-416 Netting fish.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-416.md","filePath":"vacode/29.1-416.md"}'),r={name:"vacode/29.1-416.md"},s=i('<h1 id="§-29-1-416-netting-fish" tabindex="-1">§ 29.1-416 Netting fish. <a class="header-anchor" href="#§-29-1-416-netting-fish" aria-label="Permalink to &quot;§ 29.1-416 Netting fish.&quot;">​</a></h1><p>A. The fee for a permit to net fish in inland waters, for private table use, or for sale where permitted, shall be as follows:</p><ol><li><p>County dip net, three dollars and fifty cents each per year;</p></li><li><p>Gill net, eight dollars each per year;</p></li><li><p>Haul seine, seventeen dollars and fifty cents each per year; and</p></li><li><p>Haul seine to catch species designated by the Board for sale, forty-five dollars each per year.</p></li></ol><p>B. The Board may permit a licensee to use dip nets or gill nets to take for sale fish of any designated species in the waters of Back Bay and its tributaries. However, any nonresident desiring to take or catch fish in Back Bay and its tributaries for which a permit is required and where such fishing is not prohibited, shall first pay $350 per year to the Department for a nonresident harvester&#39;s permit. Such a permit shall be required for each boat used to take or catch fish in Back Bay and its tributaries, and shall be in addition to any other permit required for the activity involved.</p><p>Code 1950, § 29-110; 1956, c. 343; 1974, c. 363; 1987, c. 488; 1988, c. 250; 1989, c. 692; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>.</p>',5),n=[s];function o(l,c,d,h,p,f){return t(),a("div",null,n)}const m=e(r,[["render",o]]);export{u as __pageData,m as default};
