import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 29.1-520 Times for hunting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-520.md","filePath":"vacode/29.1-520.md"}'),r={name:"vacode/29.1-520.md"},i=n('<h1 id="§-29-1-520-times-for-hunting" tabindex="-1">§ 29.1-520 Times for hunting. <a class="header-anchor" href="#§-29-1-520-times-for-hunting" aria-label="Permalink to &quot;§ 29.1-520 Times for hunting.&quot;">​</a></h1><p>A. Nonmigratory game birds and game animals may be hunted from one-half hour before sunrise to one-half hour after sunset. Bears may be hunted without capturing or taking from 4:00 a.m. until 10:00 p.m. during bear hound training season.</p><p>B. Fur-bearing animals and nuisance species of birds and animals may be hunted by day or by night, except that muskrats may be hunted by day only.</p><p>C. A violation of this section shall be punishable as a Class 3 misdemeanor.</p><p>Code 1950, §§ 29-141, 29-142; 1962, c. 469; 1974, c. 302; 1987, c. 488; 1989, c. 421; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0031" target="_blank" rel="noreferrer">31</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0226" target="_blank" rel="noreferrer">226</a>.</p>',5),o=[i];function s(l,c,h,d,m,u){return a(),t("div",null,o)}const g=e(r,[["render",s]]);export{f as __pageData,g as default};
