import{_ as e,o as n,c as i,z as a,a as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-526 Counties and cities may prohibit hunting or trapping near primary and secondary highways.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-526.md","filePath":"vacode/29.1-526.md"}'),t={name:"vacode/29.1-526.md"},r=a("h1",{id:"§-29-1-526-counties-and-cities-may-prohibit-hunting-or-trapping-near-primary-and-secondary-highways",tabindex:"-1"},[o("§ 29.1-526 Counties and cities may prohibit hunting or trapping near primary and secondary highways. "),a("a",{class:"header-anchor",href:"#§-29-1-526-counties-and-cities-may-prohibit-hunting-or-trapping-near-primary-and-secondary-highways","aria-label":'Permalink to "§ 29.1-526 Counties and cities may prohibit hunting or trapping near primary and secondary highways."'},"​")],-1),h=a("p",null,"The governing body of any county or city may prohibit by ordinance the hunting, with a firearm, of any game bird or game animal while the hunting is on or within 100 yards of any primary or secondary highway in such county or city and may provide that any violation of the ordinance shall be a Class 3 misdemeanor. In addition, the governing body of any county or city may prohibit by ordinance the trapping of any game animal or furbearer within fifty feet of the shoulder of any primary or secondary highway in the county or city and may provide that any violation of the ordinance shall be a Class 3 misdemeanor. No such ordinance shall prohibit such trapping where the written permission of the landowner is obtained. It shall be the duty of the governing body enacting an ordinance under the provisions of this section to notify the Director by registered mail no later than May 1 of the year in which the ordinance is to take effect. If the governing body fails to make such notice, the ordinance shall be unenforceable.",-1),s=a("p",null,'For the purpose of this section, the terms "hunt" and "trap" shall not include the necessary crossing of highways for the bona fide purpose of going into or leaving a lawful hunting or trapping area.',-1),c=a("p",null,"1962, c. 141, § 29-144.5; 1964, c. 549; 1977, c. 377; 1982, c. 194; 1987, c. 488; 1989, c. 421.",-1),d=[r,h,s,c];function y(p,l,g,m,f,u){return n(),i("div",null,d)}const w=e(t,[["render",y]]);export{_ as __pageData,w as default};
