import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 29.1-528 Board to develop model ordinances for hunting with firearms; counties or cities may adopt.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-528.md","filePath":"vacode/29.1-528.md"}'),r={name:"vacode/29.1-528.md"},a=t('<h1 id="§-29-1-528-board-to-develop-model-ordinances-for-hunting-with-firearms-counties-or-cities-may-adopt" tabindex="-1">§ 29.1-528 Board to develop model ordinances for hunting with firearms; counties or cities may adopt. <a class="header-anchor" href="#§-29-1-528-board-to-develop-model-ordinances-for-hunting-with-firearms-counties-or-cities-may-adopt" aria-label="Permalink to &quot;§ 29.1-528 Board to develop model ordinances for hunting with firearms; counties or cities may adopt.&quot;">​</a></h1><p>A. The Board shall adopt regulations establishing model ordinances for hunting with firearms that may be adopted by counties or cities. Such model ordinances shall address items including firearm caliber; type of firearm, including rifle, shotgun, or muzzleloader; type of ammunition; and the hunting of groundhogs or coyotes.</p><p>B. The governing body of any county or city may, by ordinance, (i) prohibit hunting in such county or city with a shotgun loaded with slugs, or with a rifle of a caliber larger than .22 rimfire; (ii) permit the hunting of groundhogs with a rifle of a caliber larger than .22 rimfire between March 1 and August 31; (iii) permit the use of muzzle-loading rifles during the prescribed open seasons for the hunting of game species; (iv) specify permissible types of ammunition to be used for hunting in the county or city; or (v) permit the hunting of coyotes with a rifle of a caliber larger than .22 rimfire.</p><p>C. No such ordinance shall be enforceable unless the governing body notifies the Director by registered mail prior to May 1 of the year in which the ordinance is to take effect.</p><p>D. In adopting an ordinance pursuant to the provisions of this section, the governing body of any county or city may provide that any person who violates the provisions of the ordinance is guilty of a Class 3 misdemeanor.</p><p>1976, c. 443, § 29-144.6; 1977, cc. 20, 377; 1978, c. 303; 1986, c. 342; 1987, c. 488; 1989, c. 421; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0642" target="_blank" rel="noreferrer">642</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>.</p>',6),n=[a];function s(c,d,h,l,f,p){return o(),i("div",null,n)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
