import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 29.1-530.1 Solid blaze orange or solid blaze pink clothing required at certain times.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-530.1.md","filePath":"vacode/29.1-530.1.md"}'),o={name:"vacode/29.1-530.1.md"},n=r('<h1 id="§-29-1-530-1-solid-blaze-orange-or-solid-blaze-pink-clothing-required-at-certain-times" tabindex="-1">§ 29.1-530.1 Solid blaze orange or solid blaze pink clothing required at certain times. <a class="header-anchor" href="#§-29-1-530-1-solid-blaze-orange-or-solid-blaze-pink-clothing-required-at-certain-times" aria-label="Permalink to &quot;§ 29.1-530.1 Solid blaze orange or solid blaze pink clothing required at certain times.&quot;">​</a></h1><p>A. For the purposes of this section, &quot;solid blaze orange&quot; means a safety orange or fluorescent orange hue and &quot;solid blaze pink&quot; means a safety pink or fluorescent pink hue.</p><p>B. During any firearms deer season, except during the special season for hunting deer with a muzzle-loading rifle only, in counties and cities designated by the Board, every hunter and every person accompanying a hunter shall (i) wear a solid blaze orange or solid blaze pink hat, except that the bill or brim of the hat may be a color or design other than solid blaze orange or solid blaze pink, or solid blaze orange or solid blaze pink upper body clothing that is visible from 360 degrees, (ii) display at least 100 square inches of solid blaze orange or solid blaze pink material at shoulder level within body reach visible from 360 degrees, or (iii) when hunting from an enclosed ground blind, display at least 100 square inches of solid blaze orange or solid blaze pink material visible from 360 degrees attached to or immediately above a blind.</p><p>C. During the special season for hunting deer with a muzzle-loading rifle only, in counties and cities designated by the Board, every muzzleloader deer hunter and every person accompanying a muzzleloader deer hunter shall wear (i) a solid blaze orange or solid blaze pink hat, except that the bill or brim of the hat may be a color or design other than solid blaze orange or solid blaze pink, or (ii) solid blaze orange or solid blaze pink upper body clothing, either of which shall be visible from 360 degrees, unless such person is physically located in a tree stand or other stationary hunting location.</p><p>D. Any person violating the provisions of this section shall, upon conviction, pay a fine of $25.</p><p>E. Violations of this section shall not be admissible in any civil action for personal injury or death as evidence of negligence, contributory negligence, or assumption of the risk.</p><p>F. This section shall not apply when (i) hunting waterfowl from stationary or floating blinds, (ii) hunting waterfowl over decoys, (iii) hunting waterfowl in wetlands as defined in § <a href="/vacode/28.2-1300/">28.2-1300</a>, (iv) hunting waterfowl from a boat or other floating conveyance, (v) hunting doves, (vi) participating in hunting dog field trials permitted by the Board, (vii) on horseback while hunting foxes with hounds but without firearms, or (viii) hunting with a bow and arrow in areas where the discharge of firearms is prohibited by state law or local ordinance.</p><p>1987, c. 319, § 29-147.2; 1988, cc. 474, 715; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0039" target="_blank" rel="noreferrer">39</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0167" target="_blank" rel="noreferrer">167</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0011" target="_blank" rel="noreferrer">11</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0140" target="_blank" rel="noreferrer">140</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0347" target="_blank" rel="noreferrer">347</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',8),t=[n];function l(s,d,h,c,g,p){return i(),a("div",null,t)}const u=e(o,[["render",l]]);export{f as __pageData,u as default};
