import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-530.3 Remote hunting prohibited; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-530.3.md","filePath":"vacode/29.1-530.3.md"}'),i={name:"vacode/29.1-530.3.md"},a=r('<h1 id="§-29-1-530-3-remote-hunting-prohibited-penalty" tabindex="-1">§ 29.1-530.3 Remote hunting prohibited; penalty. <a class="header-anchor" href="#§-29-1-530-3-remote-hunting-prohibited-penalty" aria-label="Permalink to &quot;§ 29.1-530.3 Remote hunting prohibited; penalty.&quot;">​</a></h1><p>A. It is unlawful for any person to engage in computer-assisted remote hunting or provide or operate a facility that allows others to engage in computer-assisted remote hunting if the wild animal or wild bird being hunted or shot is located in the Commonwealth.</p><p>B. Any person who violates this section is guilty of a Class 1 misdemeanor. In addition to the penalty prescribed herein, the court shall revoke all current hunting licenses and privileges of the person convicted of violating this section and prohibit that person from hunting for a period of one to five years. Notification of the revocation or prohibition shall be forwarded to the Department pursuant to subsections C and D of § <a href="http://law.lis.virginia.gov/vacode/18.2-56.1/" target="_blank" rel="noreferrer">18.2-56.1</a>.</p><p>C. For the purposes of this section &quot;computer-assisted remote hunting&quot; means the use of a computer or other device, equipment, or software, to remotely control the aiming and discharge of a firearm or other weapon, that allows a person, not physically present, to hunt or shoot any wild animal or wild bird.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0172" target="_blank" rel="noreferrer">172</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0226" target="_blank" rel="noreferrer">226</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0183" target="_blank" rel="noreferrer">183</a>.</p>',5),n=[a];function s(l,h,p,c,d,f){return t(),o("div",null,n)}const m=e(i,[["render",s]]);export{u as __pageData,m as default};
