import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 29.1-551 Assessment of value of game or fish unlawfully taken.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-551.md","filePath":"vacode/29.1-551.md"}'),o={name:"vacode/29.1-551.md"},n=r('<h1 id="§-29-1-551-assessment-of-value-of-game-or-fish-unlawfully-taken" tabindex="-1">§ 29.1-551 Assessment of value of game or fish unlawfully taken. <a class="header-anchor" href="#§-29-1-551-assessment-of-value-of-game-or-fish-unlawfully-taken" aria-label="Permalink to &quot;§ 29.1-551 Assessment of value of game or fish unlawfully taken.&quot;">​</a></h1><p>The judge or court, upon convicting any person of a violation of §§ <a href="http://law.lis.virginia.gov/vacode/29.1-523/" target="_blank" rel="noreferrer">29.1-523</a>, <a href="http://law.lis.virginia.gov/vacode/29.1-525.1/" target="_blank" rel="noreferrer">29.1-525.1</a>, <a href="http://law.lis.virginia.gov/vacode/29.1-530.2/" target="_blank" rel="noreferrer">29.1-530.2</a>, <a href="http://law.lis.virginia.gov/vacode/29.1-548/" target="_blank" rel="noreferrer">29.1-548</a>, <a href="http://law.lis.virginia.gov/vacode/29.1-550/" target="_blank" rel="noreferrer">29.1-550</a> or § <a href="http://law.lis.virginia.gov/vacode/29.1-552/" target="_blank" rel="noreferrer">29.1-552</a> shall, in addition to imposition of the punishment prescribed in those sections, ascertain the approximate replacement value of animals, birds or fish taken in violation of those sections and shall assess the value against the person convicted. The assessment shall be paid by the person so convicted within the time prescribed in the judgment of the judge or court, not exceeding sixty days, and the collecting officer shall forward such payments to the Board for payment into the state treasury. The Comptroller shall credit such payments to the game protection fund.</p><p>Code 1950, § 29-163.2; 1950, p. 936; 1952, c. 78; 1962, c. 469; 1976, c. 660; 1987, c. 488; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0856" target="_blank" rel="noreferrer">856</a>.</p>',3),i=[n];function s(l,c,f,h,d,p){return a(),t("div",null,i)}const u=e(o,[["render",s]]);export{v as __pageData,u as default};
