import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-574 Prohibitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-574.md","filePath":"vacode/29.1-574.md"}'),o={name:"vacode/29.1-574.md"},r=t('<h1 id="§-29-1-574-prohibitions" tabindex="-1">§ 29.1-574 Prohibitions. <a class="header-anchor" href="#§-29-1-574-prohibitions" aria-label="Permalink to &quot;§ 29.1-574 Prohibitions.&quot;">​</a></h1><p>A. No person shall knowingly import, possess, transport, sell, purchase, give, receive, or introduce into the Commonwealth any member of a species designated as a nonindigenous aquatic nuisance species without a permit from the Director issued pursuant to § <a href="/vacode/29.1-575/">29.1-575</a>.</p><p>B. Subsection A shall not apply to any person who (i) lawfully catches a snakehead fish of the family Channidae, (ii) subsequently kills such fish, and (iii) notifies the Department, as soon as practicable, of his actions.</p><p>C. Subsection A shall not apply to any Hazard Analysis and Critical Control Point (HACCP) plan (21 C.F.R. 120 et seq.) certified restaurant or retail market that purchases from an HACCP certified dealer or sells processed snakehead fish of the family Channidae.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0916" target="_blank" rel="noreferrer">916</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>.</p>',5),s=[r];function n(l,c,h,p,d,f){return a(),i("div",null,s)}const m=e(o,[["render",n]]);export{u as __pageData,m as default};
