import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 29.1-576.1 Zebra mussels; education program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-576.1.md","filePath":"vacode/29.1-576.1.md"}'),n={name:"vacode/29.1-576.1.md"},o=t('<h1 id="§-29-1-576-1-zebra-mussels-education-program" tabindex="-1">§ 29.1-576.1 Zebra mussels; education program. <a class="header-anchor" href="#§-29-1-576-1-zebra-mussels-education-program" aria-label="Permalink to &quot;§ 29.1-576.1 Zebra mussels; education program.&quot;">​</a></h1><p>The Director shall establish an education program that instructs boaters and other members of the public in methods of preventing or slowing the infestation of the waters of the Commonwealth by zebra mussels, quagga mussels, or other nonindigenous aquatic nuisance species as defined in § <a href="http://law.lis.virginia.gov/vacode/29.1-571/" target="_blank" rel="noreferrer">29.1-571</a>. The education program may be delivered through the boating safety education program required by § <a href="http://law.lis.virginia.gov/vacode/29.1-735.2/" target="_blank" rel="noreferrer">29.1-735.2</a>, by posting on the Department&#39;s website, or by other means, and shall include cleaning and draining guidelines, designated dry times for watercraft and other recreational equipment, and public outreach, including published instructions and training videos.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0540" target="_blank" rel="noreferrer">540</a>.</p>',3),i=[o];function s(d,c,l,h,u,m){return a(),r("div",null,i)}const _=e(n,[["render",s]]);export{g as __pageData,_ as default};
