import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-733.22 Replacement certificate of title.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-733.22.md","filePath":"vacode/29.1-733.22.md"}'),r={name:"vacode/29.1-733.22.md"},o=i('<h1 id="§-29-1-733-22-replacement-certificate-of-title" tabindex="-1">§ 29.1-733.22 Replacement certificate of title. <a class="header-anchor" href="#§-29-1-733-22-replacement-certificate-of-title" aria-label="Permalink to &quot;§ 29.1-733.22 Replacement certificate of title.&quot;">​</a></h1><p>A. If a written certificate of title is lost, stolen, mutilated, destroyed, or otherwise becomes unavailable or illegible, the secured party of record or, if no secured party is indicated in the files of the Department, the owner of record may apply for and, by furnishing information satisfactory to the Department, obtain a replacement certificate in the name of the owner of record.</p><p>B. An applicant for a replacement certificate of title shall sign the application and, except as otherwise permitted by the Department, the application shall comply with § <a href="http://law.lis.virginia.gov/vacode/29.1-733.7/" target="_blank" rel="noreferrer">29.1-733.7</a>. The application shall include the existing certificate unless the certificate is lost, stolen, mutilated, destroyed, or otherwise unavailable.</p><p>C. A replacement certificate of title created by the Department shall comply with § <a href="http://law.lis.virginia.gov/vacode/29.1-733.9/" target="_blank" rel="noreferrer">29.1-733.9</a> and indicate on the face of the certificate that it is a replacement certificate.</p><p>D. If a person receiving a replacement certificate of title subsequently obtains possession of the original written certificate, the person promptly shall destroy the original certificate of title.</p><p>2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>.</p>',6),c=[o];function n(l,s,p,f,h,d){return t(),a("div",null,c)}const g=e(r,[["render",n]]);export{_ as __pageData,g as default};
