import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-738.02 Persons under age twenty-one operating watercraft after illegally consuming alcohol; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-738.02.md","filePath":"vacode/29.1-738.02.md"}'),r={name:"vacode/29.1-738.02.md"},n=a('<h1 id="§-29-1-738-02-persons-under-age-twenty-one-operating-watercraft-after-illegally-consuming-alcohol-penalty" tabindex="-1">§ 29.1-738.02 Persons under age twenty-one operating watercraft after illegally consuming alcohol; penalty. <a class="header-anchor" href="#§-29-1-738-02-persons-under-age-twenty-one-operating-watercraft-after-illegally-consuming-alcohol-penalty" aria-label="Permalink to &quot;§ 29.1-738.02 Persons under age twenty-one operating watercraft after illegally consuming alcohol; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person under the age of twenty-one to operate any watercraft or motorboat upon the waters of the Commonwealth after consuming alcohol. Any such person with a blood alcohol concentration of 0.02 percent or more by weight by volume or 0.02 grams or more per 210 liters of breath but less than 0.08 by weight by volume or less than 0.08 grams per 210 liters of breath as indicated by a chemical test administered as provided in Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/18.2-266/" target="_blank" rel="noreferrer">18.2-266</a> et seq.) of Chapter 7 of Title 18.2 shall be in violation of this section.</p><p>B. A violation of this section shall be punishable by denial by the court of such person&#39;s privilege to operate a watercraft or motorboat for a period of six months from the date of conviction and by a fine of not more than $500. Any person convicted of a violation of this section shall be eligible to attend an Alcohol Safety Action Program under the provisions of § <a href="http://law.lis.virginia.gov/vacode/29.1-738.5/" target="_blank" rel="noreferrer">29.1-738.5</a>.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0631" target="_blank" rel="noreferrer">631</a>.</p>',4),l=[n];function i(s,c,h,f,p,d){return t(),o("div",null,l)}const m=e(r,[["render",i]]);export{_ as __pageData,m as default};
