import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 29.1-744.3 Slacken speed and control wakes near structures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-744.3.md","filePath":"vacode/29.1-744.3.md"}'),o={name:"vacode/29.1-744.3.md"},n=r('<h1 id="§-29-1-744-3-slacken-speed-and-control-wakes-near-structures" tabindex="-1">§ 29.1-744.3 Slacken speed and control wakes near structures. <a class="header-anchor" href="#§-29-1-744-3-slacken-speed-and-control-wakes-near-structures" aria-label="Permalink to &quot;§ 29.1-744.3 Slacken speed and control wakes near structures.&quot;">​</a></h1><p>It shall be unlawful to operate any motorboat, except a personal watercraft, at a speed greater than the slowest possible speed required to maintain steerage and headway when within 50 feet or less of docks, piers, boathouses, boat ramps, or a person in the water, unless such person in the water (i) is being towed by the motorboat or (ii) is accompanying the motorboat, provided that such motorboat is propelled by an inboard motor or a means of propulsion that is below the water line and forward of (a) the transom or (b) an integrated swim platform.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0857" target="_blank" rel="noreferrer">857</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0700" target="_blank" rel="noreferrer">700</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0637" target="_blank" rel="noreferrer">637</a>.</p>',3),s=[n];function i(l,c,p,d,h,_){return a(),t("div",null,s)}const g=e(o,[["render",i]]);export{u as __pageData,g as default};
