import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 29.1-745 Enforcement of chapter; vessels displaying Coast Guard inspection decal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-745.md","filePath":"vacode/29.1-745.md"}'),o={name:"vacode/29.1-745.md"},n=i('<h1 id="§-29-1-745-enforcement-of-chapter-vessels-displaying-coast-guard-inspection-decal" tabindex="-1">§ 29.1-745 Enforcement of chapter; vessels displaying Coast Guard inspection decal. <a class="header-anchor" href="#§-29-1-745-enforcement-of-chapter-vessels-displaying-coast-guard-inspection-decal" aria-label="Permalink to &quot;§ 29.1-745 Enforcement of chapter; vessels displaying Coast Guard inspection decal.&quot;">​</a></h1><p>A. Every conservation police officer, officer of the Virginia Marine Police, and other law-enforcement officer of the Commonwealth and its subdivisions shall have the authority to enforce the provisions of this chapter and shall have authority to stop, board, and inspect any vessel subject to this chapter after having identified himself in his official capacity. Except for enforcement of § <a href="http://law.lis.virginia.gov/vacode/29.1-738/" target="_blank" rel="noreferrer">29.1-738</a> and the requirement of having the registration certificate on board, the provisions of this subsection shall not apply to any vessel of 26 feet or more in length on which is displayed a current valid United States Coast Guard or United States Coast Guard Auxiliary inspection decal.</p><p>B. Notwithstanding the provisions of subsection A, no conservation police officer, officer of the Virginia Marine Police, or other law-enforcement officer shall, without the consent of the owner, stop, board, or inspect any noncommercial vessel subject to this chapter unless such officer has reasonable suspicion that a violation of law or regulation exists, except that conservation police officers and officers of the Virginia Marine Police may conduct lawful stops or boardings to inspect hunting, fishing, and trapping licenses pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/28.2-231/" target="_blank" rel="noreferrer">28.2-231</a> and <a href="http://law.lis.virginia.gov/vacode/29.1-337/" target="_blank" rel="noreferrer">29.1-337</a> or to inspect creel and bag limits pursuant to § <a href="http://law.lis.virginia.gov/vacode/29.1-209/" target="_blank" rel="noreferrer">29.1-209</a> and may conduct lawful boating safety checkpoints in accordance with established agency policy.</p><p>1960, c. 500, § 62-174.17; 1964, c. 654; 1968, c. 659, § 62.1-184; 1980, c. 567; 1987, c. 488; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0484" target="_blank" rel="noreferrer">484</a>.</p>',4),r=[n];function s(c,l,f,h,p,d){return a(),t("div",null,r)}const u=e(o,[["render",s]]);export{v as __pageData,u as default};
