import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 29.1-748.1 Local ordinances; operation on lakes; minimum distance from shoreline; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/29.1-748.1.md","filePath":"vacode/29.1-748.1.md"}'),n={name:"vacode/29.1-748.1.md"},t=i('<h1 id="§-29-1-748-1-local-ordinances-operation-on-lakes-minimum-distance-from-shoreline-penalty" tabindex="-1">§ 29.1-748.1 Local ordinances; operation on lakes; minimum distance from shoreline; penalty. <a class="header-anchor" href="#§-29-1-748-1-local-ordinances-operation-on-lakes-minimum-distance-from-shoreline-penalty" aria-label="Permalink to &quot;§ 29.1-748.1 Local ordinances; operation on lakes; minimum distance from shoreline; penalty.&quot;">​</a></h1><p>A. Any locality in Planning District 23 may, by ordinance, prohibit the operation of a personal watercraft on a public lake measuring less than 50 acres in extent. Such ordinance shall provide that a violation of the prohibition constitutes a Class 4 misdemeanor. Any violation of such an ordinance shall be subject to the provisions of subsection C of § <a href="/vacode/29.1-748/">29.1-748</a>.</p><p>B. The City of Virginia Beach may, by ordinance, regulate in any portion of a waterway located solely within its territorial limits, the minimum distance that personal watercraft may be operated from the shoreline in excess of the slowest possible speed required to maintain steerage and headway. Such ordinance shall provide for distances of 100 feet from the shoreline and 200 feet from swimmers in ocean waters, and shall provide for local enforcement and penalties not exceeding those applicable to Class 4 misdemeanors. Nothing in this subsection prohibits access to and from waters where operation is not otherwise restricted.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0426" target="_blank" rel="noreferrer">426</a>.</p>',4),r=[t];function s(l,c,d,h,p,m){return a(),o("div",null,r)}const u=e(n,[["render",s]]);export{_ as __pageData,u as default};
